#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../node_modules/.pnpm/ts-jest@29.4.0_@babel+core@7.28.0_@jest+transform@30.0.4_@jest+types@30.0.1_babel-jest@29.7.0_t2utndxldispbcltnwul3iuwci/node_modules/ts-jest/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../node_modules/.pnpm/ts-jest@29.4.0_@babel+core@7.28.0_@jest+transform@30.0.4_@jest+types@30.0.1_babel-jest@29.7.0_t2utndxldispbcltnwul3iuwci/node_modules/ts-jest/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../node_modules/.pnpm/ts-jest@29.4.0_@babel+core@7.28.0_@jest+transform@30.0.4_@jest+types@30.0.1_babel-jest@29.7.0_t2utndxldispbcltnwul3iuwci/node_modules/ts-jest/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../node_modules/.pnpm/ts-jest@29.4.0_@babel+core@7.28.0_@jest+transform@30.0.4_@jest+types@30.0.1_babel-jest@29.7.0_t2utndxldispbcltnwul3iuwci/node_modules/ts-jest/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
