#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../../typeorm@0.3.25_better-sqlite3@11.0.0_pg@8.16.3_reflect-metadata@0.1.14_sqlite3@5.1.7_ts-node@_6v3tvnkvn5reeq7ndk4cy7rxy4/node_modules/typeorm/cli-ts-node-commonjs.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../../typeorm@0.3.25_better-sqlite3@11.0.0_pg@8.16.3_reflect-metadata@0.1.14_sqlite3@5.1.7_ts-node@_6v3tvnkvn5reeq7ndk4cy7rxy4/node_modules/typeorm/cli-ts-node-commonjs.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../../typeorm@0.3.25_better-sqlite3@11.0.0_pg@8.16.3_reflect-metadata@0.1.14_sqlite3@5.1.7_ts-node@_6v3tvnkvn5reeq7ndk4cy7rxy4/node_modules/typeorm/cli-ts-node-commonjs.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../../../typeorm@0.3.25_better-sqlite3@11.0.0_pg@8.16.3_reflect-metadata@0.1.14_sqlite3@5.1.7_ts-node@_6v3tvnkvn5reeq7ndk4cy7rxy4/node_modules/typeorm/cli-ts-node-commonjs.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
